const items = $input.all();

// Initialize sections with enhanced styling
let rssSection = `
<div style="margin-bottom: 40px;">
    <h2 style="
        background: linear-gradient(135deg, #f59e0b, #d97706);
        color: white;
        padding: 20px 25px;
        margin: 0 0 25px 0;
        border-radius: 12px;
        font-size: 1.8em;
        font-weight: 700;
        box-shadow: 0 4px 15px rgba(245, 158, 11, 0.3);
        display: flex;
        align-items: center;
        gap: 12px;
    ">
        University & Global News Brief
    </h2>
</div>`;

let tavilySection = `
<div style="margin-bottom: 40px;">
    <h2 style="
        background: linear-gradient(135deg, #10b981, #059669);
        color: white;
        padding: 20px 25px;
        margin: 0 0 25px 0;
        border-radius: 12px;
        font-size: 1.8em;
        font-weight: 700;
        box-shadow: 0 4px 15px rgba(16, 185, 129, 0.3);
        display: flex;
        align-items: center;
        gap: 12px;
    ">
        üîç Search Results
    </h2>
</div>`;

// Iterate through all items
for (const item of items) {
    // Handle RSS feed items
    if (item.json.text && item.json.title && item.json.link) {
        rssSection += `
            <div style="
                background: linear-gradient(135deg, #fef3c7, #fde68a);
                border-left: 5px solid #f59e0b;
                padding: 25px;
                margin-bottom: 25px;
                border-radius: 0 12px 12px 0;
                box-shadow: 0 4px 12px rgba(245, 158, 11, 0.15);
                transition: all 0.3s ease;
            ">
                <h3 style="
                    color: #92400e;
                    font-size: 1.3em;
                    font-weight: 700;
                    margin-bottom: 15px;
                    line-height: 1.4;
                ">${item.json.title}</h3>
                <p style="
                    color: #78350f;
                    font-size: 1em;
                    line-height: 1.6;
                    margin-bottom: 20px;
                ">${item.json.text}</p>
                <a href="${item.json.link}" style="
                    display: inline-block;
                    background: linear-gradient(135deg, #f59e0b, #d97706);
                    color: white;
                    text-decoration: none;
                    padding: 12px 20px;
                    border-radius: 8px;
                    font-size: 0.95em;
                    font-weight: 600;
                    box-shadow: 0 4px 12px rgba(245, 158, 11, 0.3);
                ">Read Full Article ‚Üí</a>
            </div>
        `;
    }

    // Handle Tavily search results
    if (item.json.results && Array.isArray(item.json.results)) {
        for (const result of item.json.results) {
            tavilySection += `
                <div style="
                    background: linear-gradient(135deg, #d1fae5, #a7f3d0);
                    border-left: 5px solid #10b981;
                    padding: 22px;
                    margin-bottom: 20px;
                    border-radius: 0 12px 12px 0;
                    box-shadow: 0 4px 12px rgba(16, 185, 129, 0.15);
                    transition: all 0.3s ease;
                ">
                    <h3 style="
                        color: #047857;
                        font-size: 1.2em;
                        font-weight: 700;
                        margin-bottom: 15px;
                        line-height: 1.4;
                    ">${result.title}</h3>
                    <a href="${result.url}" style="
                        display: inline-block;
                        background: linear-gradient(135deg, #10b981, #059669);
                        color: white;
                        text-decoration: none;
                        padding: 10px 18px;
                        border-radius: 8px;
                        font-size: 0.9em;
                        font-weight: 600;
                        box-shadow: 0 4px 12px rgba(16, 185, 129, 0.3);
                    ">Visit Source ‚Üí</a>
                </div>
            `;
        }
    }
}

// Combine sections into enhanced HTML output
const htmlOutput = `
    <div style="
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
        max-width: 800px;
        margin: 0 auto;
        padding: 20px;
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        min-height: 100vh;
    ">
        <div style="
            background: white;
            border-radius: 16px;
            box-shadow: 0 20px 40px rgba(0,0,0,0.1);
            overflow: hidden;
        ">
            <div style="
                background: linear-gradient(135deg, #4f46e5 0%, #7c3aed 100%);
                color: white;
                padding: 40px 30px;
                text-align: center;
            ">
                <h1 style="
                    margin: 0;
                    font-size: 2.8em;
                    font-weight: 700;
                "> Daily AI News Summary</h1>
                <p style="
                    margin: 15px 0 0 0;
                    opacity: 0.95;
                    font-size: 1.2em;
                "></p>
            </div>
            <div style="padding: 35px;">
                ${rssSection}
                ${tavilySection}
            </div>
            <div style="
                background: linear-gradient(135deg, #f8fafc, #f1f5f9);
                padding: 25px 35px;
                text-align: center;
                color: #64748b;
                font-size: 0.95em;
                border-top: 1px solid #e2e8f0;
            ">
                Generated on ${new Date().toLocaleDateString('en-US', { 
                  weekday: 'long', 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric' 
                })} at ${new Date().toLocaleTimeString('en-US', { 
                  hour: '2-digit', 
                  minute: '2-digit' 
                })}
            </div>
        </div>
    </div>
`;

return [{ json: { html: htmlOutput } }];
